/* STEP 1: Use the K-means function to partition the data set into K clusters. */

SET SCHEMA DM_PAL;DROP TYPE PAL_KMEANS_RESASSIGN_T;CREATE TYPE PAL_KMEANS_RESASSIGN_T AS TABLE(    "ID" INT,    "CENTER_ASSIGN" INT,    "DISTANCE" DOUBLE);DROP TYPE PAL_KMEANS_DATA_T;CREATE TYPE PAL_KMEANS_DATA_T AS TABLE(    "ID" INT,
    "AGE" DOUBLE,    "INCOME" DOUBLE,    PRIMARY KEY("ID"));DROP TYPE PAL_KMEANS_CENTERS_T;CREATE TYPE PAL_KMEANS_CENTERS_T AS TABLE(    "CENTER_ID" INT,    "V000" DOUBLE,    "V001" DOUBLE);DROP TYPE PAL_CONTROL_T;CREATE TYPE PAL_CONTROL_T AS TABLE(    "NAME" VARCHAR(100),    "INTARGS" INTEGER,    "DOUBLEARGS" DOUBLE,    "STRINGARGS" VARCHAR(100));  /* create kmeans procedure */DROP TABLE PAL_KMEANS_PDATA_TBL;CREATE COLUMN TABLE PAL_KMEANS_PDATA_TBL("POSITION" INTEGER,"SCHEMA_NAME" VARCHAR(100),"TYPE_NAME" VARCHAR(100),"PARAMETER_TYPE" VARCHAR(100));INSERT INTO PAL_KMEANS_PDATA_TBL VALUES (1, 'DM_PAL', 'PAL_KMEANS_DATA_T', 'IN');INSERT INTO PAL_KMEANS_PDATA_TBL VALUES (2, 'DM_PAL', 'PAL_CONTROL_T', 'IN');INSERT INTO PAL_KMEANS_PDATA_TBL VALUES (3, 'DM_PAL', 'PAL_KMEANS_RESASSIGN_T','OUT');INSERT INTO PAL_KMEANS_PDATA_TBL VALUES (4, 'DM_PAL', 'PAL_KMEANS_CENTERS_T','OUT');CALL SYS.AFLLANG_WRAPPER_PROCEDURE_DROP('DM_PAL', 'PAL_KMEANS_PROC');CALL SYS.AFLLANG_WRAPPER_PROCEDURE_CREATE('AFLPAL', 'KMEANS', 'DM_PAL','PAL_KMEANS_PROC', PAL_KMEANS_PDATA_TBL);DROP TABLE PAL_KMEANS_DATA_TBL;CREATE COLUMN TABLE PAL_KMEANS_DATA_TBL LIKE PAL_KMEANS_DATA_T;INSERT INTO PAL_KMEANS_DATA_TBL VALUES (0 , 20, 100000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (1 , 21, 101000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (2 , 22, 102000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (3 , 30, 200000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (4 , 31, 201000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (5 , 32, 202000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (6 , 40, 400000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (7 , 41, 401000);INSERT INTO PAL_KMEANS_DATA_TBL VALUES (8 , 42, 402000);DROP TABLE #PAL_CONTROL_TBL;CREATE LOCAL TEMPORARY COLUMN TABLE #PAL_CONTROL_TBL(    "NAME" VARCHAR(100),    "INTARGS" INTEGER,    "DOUBLEARGS" DOUBLE,    "STRINGARGS" VARCHAR(100));INSERT INTO #PAL_CONTROL_TBL VALUES ('THREAD_NUMBER', 2, NULL, NULL);INSERT INTO #PAL_CONTROL_TBL VALUES ('GROUP_NUMBER', 3, NULL, NULL);INSERT INTO #PAL_CONTROL_TBL VALUES ('INIT_TYPE', 1, NULL, NULL);INSERT INTO #PAL_CONTROL_TBL VALUES ('DISTANCE_LEVEL', 2, NULL, NULL);INSERT INTO #PAL_CONTROL_TBL VALUES ('MAX_ITERATION', 100, NULL, NULL);INSERT INTO #PAL_CONTROL_TBL VALUES ('EXIT_THRESHOLD', NULL, 0.000001, NULL);INSERT INTO #PAL_CONTROL_TBL VALUES ('NORMALIZATION', 0, NULL, NULL);  /* clean kmeans result */DROP TABLE PAL_KMEANS_RESASSIGN_TBL;CREATE COLUMN TABLE PAL_KMEANS_RESASSIGN_TBL LIKE PAL_KMEANS_RESASSIGN_T;DROP TABLE PAL_KMEANS_CENTERS_TBL;CREATE COLUMN TABLE PAL_KMEANS_CENTERS_TBL LIKE PAL_KMEANS_CENTERS_T;CALL DM_PAL.PAL_KMEANS_PROC(PAL_KMEANS_DATA_TBL, "#PAL_CONTROL_TBL",PAL_KMEANS_RESASSIGN_TBL, PAL_KMEANS_CENTERS_TBL) WITH OVERVIEW;SELECT * FROM PAL_KMEANS_CENTERS_TBL;SELECT * FROM PAL_KMEANS_RESASSIGN_TBL;
DROP TABLE PAL_KMEANS_RESULT_TBL;CREATE COLUMN TABLE PAL_KMEANS_RESULT_TBL(    "AGE" DOUBLE,    "INCOME" DOUBLE,    "LEVEL" INT);TRUNCATE TABLE PAL_KMEANS_RESULT_TBL;INSERT INTO PAL_KMEANS_RESULT_TBL(    SELECT PAL_KMEANS_DATA_TBL.AGE, PAL_KMEANS_DATA_TBL.INCOME,PAL_KMEANS_RESASSIGN_TBL.CENTER_ASSIGN    FROM PAL_KMEANS_RESASSIGN_TBL    INNER JOIN PAL_KMEANS_DATA_TBL        ON PAL_KMEANS_RESASSIGN_TBL.ID = PAL_KMEANS_DATA_TBL.IDSELECT * FROM PAL_KMEANS_RESULT_TBL;


/* STEP 2: Use the above output as the training data of C4.5 Decision Tree. */

