CREATE TYPE PAL_PCDT_RESULT_T AS TABLE(    "ID" INT,    "CLASSLABEL" VARCHAR(50));   /*create procedure */DROP TABLE PAL_PCDT_PDATA_TBL;CREATE COLUMN TABLE PAL_PCDT_PDATA_TBL(    "POSITION" INTEGER,    "SCHEMA_NAME" VARCHAR(100),    "TYPE_NAME" VARCHAR(100),    "PARAMETER_TYPE" VARCHAR(100));INSERT INTO PAL_PCDT_PDATA_TBL VALUES (1, 'DM_PAL', 'PAL_PCDT_DATA_T', 'IN');INSERT INTO PAL_PCDT_PDATA_TBL VALUES (2, 'DM_PAL', 'PAL_CONTROL_T', 'IN');INSERT INTO PAL_PCDT_PDATA_TBL VALUES (3, 'DM_PAL', 'PAL_PCDT_JSONMODEL_T','IN');INSERT INTO PAL_PCDT_PDATA_TBL VALUES (4, 'DM_PAL', 'PAL_PCDT_RESULT_T', 'OUT');CALL SYS.AFLLANG_WRAPPER_PROCEDURE_DROP('DM_PAL', 'PAL_PREDICTWITHDT_PROC');CALL SYS.AFLLANG_WRAPPER_PROCEDURE_CREATE('AFLPAL', 'PREDICTWITHDT', 'DM_PAL','PAL_PREDICTWITHDT_PROC', PAL_PCDT_PDATA_TBL);DROP TABLE PAL_PCDT_DATA_TBL;CREATE COLUMN TABLE PAL_PCDT_DATA_TBL LIKE PAL_PCDT_DATA_T;INSERT INTO PAL_PCDT_DATA_TBL VALUES (10, 20, 100003);INSERT INTO PAL_PCDT_DATA_TBL VALUES (11, 30, 200003);INSERT INTO PAL_PCDT_DATA_TBL VALUES (12, 40, 400003);DROP TABLE #PAL_CONTROL_TBL;CREATE LOCAL TEMPORARY COLUMN TABLE #PAL_CONTROL_TBL(    "NAME" VARCHAR (100),    "INTARGS" INTEGER,    "DOUBLEARGS" DOUBLE,    "STRINGARGS" VARCHAR (100));INSERT INTO #PAL_CONTROL_TBL VALUES ('THREAD_NUMBER', 2, NULL, NULL);DROP TABLE PAL_PCDT_RESULT_TBL;CREATE COLUMN TABLE PAL_PCDT_RESULT_TBL LIKE PAL_PCDT_RESULT_T;CALL DM_PAL.PAL_PREDICTWITHDT_PROC(PAL_PCDT_DATA_TBL, "#PAL_CONTROL_TBL",PAL_CDT_JSONMODEL_TBL, PAL_PCDT_RESULT_TBL) WITH OVERVIEW;SELECT * FROM PAL_PCDT_RESULT_TBL;
